<?php// no direct accessdefined('_JEXEC') or die('Restricted access');$day_first = true;list($day, $month) = explode(' ', date('j M', strtotime($this->item->created)));$colcount = 0;$canEdit	= ($this->user->authorize('com_content', 'edit', 'content', 'all') || $this->user->authorize('com_content', 'edit', 'content', 'own'));?><?php if ($this->item->state == 0) : ?>    <div class="system-unpublished">     <?php endif; ?><?php if ($canEdit || $this->item->params->get('show_title') || $this->item->params->get('show_pdf_icon') || $this->item->params->get('show_print_icon') || $this->item->params->get('show_email_icon') || $this->item->params->get('show_create_date') || ($this->item->params->get('show_author') && $this->item->author != "")) : ?><table class="contentpaneopen<?php echo $this->item->params->get( 'pageclass_sfx' ); ?>"><?php if ($canEdit || $this->item->params->get('show_title') || $this->item->params->get('show_create_date')) : ?><tr>	<?php if ($this->item->params->get('show_create_date')) : ?>	    <td align="center" valign="top" class="calendar" width="60" height="30">            <?php if ($day_first) : ?>                <div class="day"><?php echo $day; ?>				</div>				                    <div class="month"><?php echo JText::_($month); ?>					</div>            <?php else : ?>		                <div class="month"><?php echo JText::_($month); ?>		                </div>		                    <div class="day"><?php echo $day; ?>		                    </div>    <?php endif; ?>                    <img src="templates/everhest/images/blank.gif" width="60" height="1" alt="" border="0" />        </td>                <td nowrap="nowrap"> &nbsp; &nbsp; </td>	<?php 	$colcount += 2; ?>     <?php endif; ?>	<?php if ($this->item->params->get('show_title')) : ?>	<td class="contentheading<?php echo $this->item->params->get( 'pageclass_sfx' ); ?>" valign="middle" width="100%">		<?php if ($this->item->params->get('link_titles') && $this->item->readmore_link != '') : ?>	<a href="<?php echo $this->item->readmore_link; ?>" class="contentpagetitle<?php echo $this->item->params->get( 'pageclass_sfx' ); ?>">	<?php echo $this->item->title; ?></a>		<?php else : ?>			<?php echo $this->escape($this->item->title); ?>		<?php endif; ?>	</td>	<?php 	$colcount++; ?>	<?php endif; ?>	<?php if ($canEdit) : ?>	<td>		<?php echo JHTML::_('icon.edit', $this->item, $this->item->params, $this->access); ?>	</td>	<?php 	$colcount++; ?>	<?php endif; ?></tr><?php endif; ?><?php if ($this->item->params->get('show_pdf_icon') || $this->item->params->get('show_print_icon') || $this->item->params->get('show_email_icon') || ($this->item->params->get('show_author') && $this->item->author != "")) : ?><tr><td<?php if ($colcount > 1) echo ' colspan="' . $colcount . '"'; ?>><div class="contentinfo"><?php$elements = array();if ($this->item->params->get('show_author') && $this->item->author != "")	$elements[] = '<span class="author">' . JText::sprintf( 'Written by', ($this->item->created_by_alias ? $this->item->created_by_alias : $this->item->author) ) . '</span>';if ($this->item->params->get('show_pdf_icon'))	$elements[] = JHTML::_('icon.pdf', $this->item, $this->item->params, $this->access);if ( $this->item->params->get( 'show_print_icon' ))	$elements[] = JHTML::_('icon.print_popup', $this->item, $this->item->params, $this->access);if ($this->item->params->get('show_email_icon'))	$elements[] = JHTML::_('icon.email', $this->item, $this->item->params, $this->access);echo implode(' &nbsp; ', $elements);?></div></td></tr><?php endif; ?></table><?php endif; ?><?php  if (!$this->item->params->get('show_intro')) :	echo $this->item->event->afterDisplayTitle;endif; ?><?php echo $this->item->event->beforeDisplayContent; ?><table class="contentpaneopen<?php echo $this->item->params->get( 'pageclass_sfx' ); ?>"><?php if (($this->item->params->get('show_section') && $this->item->sectionid) || ($this->item->params->get('show_category') && $this->item->catid)) : ?><tr>	<td>		<?php if ($this->item->params->get('show_section') && $this->item->sectionid && isset($this->item->section)) : ?>		<span>			<?php if ($this->item->params->get('link_section')) : ?>				<?php echo '<a href="'.JRoute::_(ContentHelperRoute::getSectionRoute($this->item->sectionid)).'">'; ?>			<?php endif; ?>			<?php echo $this->item->section; ?>			<?php if ($this->item->params->get('link_section')) : ?>				<?php echo '</a>'; ?>			<?php endif; ?>				<?php if ($this->item->params->get('show_category')) : ?>				<?php echo ' - '; ?>			<?php endif; ?>		</span>		<?php endif; ?>		<?php if ($this->item->params->get('show_category') && $this->item->catid) : ?>		<span>			<?php if ($this->item->params->get('link_category')) : ?>				<?php echo '<a href="'.JRoute::_(ContentHelperRoute::getCategoryRoute($this->item->catslug, $this->item->sectionid)).'">'; ?>			<?php endif; ?>			<?php echo $this->item->category; ?>			<?php if ($this->item->params->get('link_category')) : ?>				<?php echo '</a>'; ?>			<?php endif; ?>		</span>		<?php endif; ?>	</td></tr><?php endif; ?><?php if ($this->item->params->get('show_url') && $this->item->urls) : ?><tr>	<td valign="top" colspan="2">		<a href="http://<?php echo $this->item->urls ; ?>" target="_blank">			<?php echo $this->item->urls; ?></a>	</td></tr><?php endif; ?><tr><td valign="top" colspan="2"><?php if (isset ($this->item->toc)) : ?>	<?php echo $this->item->toc; ?><?php endif; ?><?php echo $this->item->text; ?></td></tr><?php if ($this->item->params->get('show_readmore') && $this->item->readmore) : ?><tr>	<td  colspan="2">		<a href="<?php echo $this->item->readmore_link; ?>" class="readon<?php echo $this->item->params->get('pageclass_sfx'); ?>">			<?php if ($this->item->readmore_register) :				echo JText::_('Register to read more...');			elseif ($readmore = $this->item->params->get('readmore')) :				echo $readmore;			else :				echo JText::sprintf('Read more...');			endif; ?></a>	</td></tr><?php endif; ?></table><?php if ($this->item->state == 0) : ?></div><?php endif; ?><span class="article_separator">&nbsp;</span><?php echo $this->item->event->afterDisplayContent; ?>